public class Part1 {
      public int findStopCodon(String dna, int startCodon, String stopCodon) {
        int currIndex = dna.indexOf(stopCodon, startCodon + 3);
        while(currIndex != -1) {
            int diff = currIndex - startCodon;
            if(diff % 3 == 0) {
                return currIndex;
            } else {
                currIndex = dna.indexOf(stopCodon, currIndex + 1);
            }
        }
        return dna.length();
    }
    
    public String findGene(String dna, int start)
    {
        int startIndex = dna.indexOf("ATG", start);
        if (startIndex == -1)
        {
            return "";
        }
        int taaIndex = findStopCodon(dna, startIndex, "TAA");
        int tagIndex = findStopCodon(dna, startIndex, "TAG");
        int tgaIndex = findStopCodon(dna, startIndex, "TGA");
        int minIndex = Math.min(taaIndex, Math.min(tagIndex, tgaIndex));
        if (minIndex == dna.length())
        {
            return "";
        }
        return dna.substring(startIndex, minIndex + 3);
    }
    
    public void printAllGenes(String dna)
    {
        int start = 0;
        String gene;
        while (true)
        {
            gene = findGene(dna, start);
            System.out.println(gene);
            if (gene.isEmpty())
            {
                break;
            }
            start += gene.length() + 1;
        }
    }
     public StorageResource getAllGenes(String dna)
    {
        StorageResource sr = new StorageResource();
        int start = 0;
        String gene;
        while (true)
        {
            gene = findGene(dna, start);
            sr.add(gene);
            if (gene.isEmpty())
            {
                break;
            }
            start += gene.length() + 1;
        }
        return sr;
    }
    public void testFindStopCodon(){
        System.out.println(findGene("ATGTAA", 0));
        System.out.println(findGene("AGTAXATGTTTATTAA", 0 ));
        System.out.println(findGene("ATGAAAAATTGAAGGGTAA", 0));
        System.out.println(findGene("ATGAAAGGGTTTTAGATGTAAATGTAAATGTAA", 0));
    }
    
    public void testFindAllGenes(){
        printAllGenes("");
    }
     
    public void testGetAllGenes(){
        StorageResource sr = getAllGenes("AATGCTAACTAGCTGACTAAT");
        for (String s : sr.data())
        {
            System.out.println(s);
        }
    }
}
